import React from "react";
import styles from './Categories.module.css'

class Categories extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            activeItem: 0,
            productCard: this.props.productCards
        }
    }

    // componentDidUpdate(prevProps, prevState) {
    //     if (prevState.productCards !== this.state.productCards) {
    //         this.onSelectCategories()
    //     }
    // }

    onSelectCategories = index => {
        this.setState({
            activeItem: index,
            productCard: this.props.productCards[index].products
        })
    }

    render() {
        console.log(this.props)
        const {productCards} = this.props
        console.log(productCards[2])
        console.log(this.state.activeItem)
        console.log(this.state.productCard)
        let select = this.state.productCard
        console.log(select)

        return (
            <div>
                <ul className={styles.categories}>
                    {
                        productCards &&
                        productCards.map((category, index) => (
                            // select &&
                            // select.map((category, index) => (
                            <li onClick={() => this.onSelectCategories(index)}
                                key={`${category.name}_${index}`}
                                className={`${styles.categoriesItem} 
                                ${this.state.activeItem === index ? styles.active : ''}`}>
                                {category.name}
                            </li>))
                    }
                </ul>
            </div>
        )
    }
}

export default Categories
