    render() {
        // console.log(this.props.orders.brand)
        const{orderItem, activeCurrency} = this.props
        console.log(activeCurrency)
        return(
            <main className={styles.container}>
                {/*{orders &&*/}
                {/*   orders.map((itemOrder, id) => (*/}
                
