import { createSlice } from '@reduxjs/toolkit'

import { fetchCategories } from "./fetchCategories";
import {getCurrencies} from "./getCurrencies";


const initialState = {
  activeCurrency: 0,
  selectedCurrency: "$",
  openPopup: false,
  activeCategory: 0,
  currencies: Promise.resolve(getCurrencies(0)).then(res => res),
  status: "loading",
  value: 555
}

export const currenciesSlice = createSlice({
  name: 'currencies',
  initialState,
  reducers: {
    addItem: (state, action) => {
      const findItem = state.items.find((obj) => obj.id === action.payload.id)
      if (findItem) {
        findItem.count++
      } else {
        state.items.push({
          ...action.payload,
          count: 1,
        })
      }

      // state.totalPrice = calcTotalPrice(state.items)
    },

    increment: state => {
      state.value += 1;
    },

    setItems: (state, action) => {
      state.currencies = action.payload
    },

    onSelectCurrencies: (state, action) => {
      state.activeCurrency = action.payload
      state.selectedCurrency = state.currencies[action.payload].currency.symbol
      state.openPopup = false
    }

  },
  // extraReducers: (builder) => {
  //   builder.addCase(fetchCategories.pending, (state) => {
  //     state.status = 'loading'
  //     state.currencies = []
  //   })
  //   builder.addCase(fetchCategories.fulfilled, (state, action) => {
  //     state.currencies = action.payload
  //     state.status = 'success'
  //   })
  //   builder.addCase(fetchCategories.rejected, (state) => {
  //     state.status = 'error'
  //     state.currencies = []
  //   })
  // },
})


export const { setItems, increment, onSelectCurrencies } = currenciesSlice.actions

export default currenciesSlice.reducer
