import {fetchCategories} from "./fetchCategories";
import {connect} from "react-redux";
import { onSelectCurrencies } from "./slice";

export const getCurrencies = async () => {
  const result = await fetchCategories();
  return result.data.categories[this.props.activeCategory].products[0].prices;
}

const mapStateToProps = (state) => ({
  activeCurrency: state.currencies.activeCurrency,
});

const mapDispatchToProps = { onSelectCurrencies };

export default connect(mapStateToProps, mapDispatchToProps)(getCurrencies);
