import {fetchCategories} from "./fetchCategories";
import {connect} from "react-redux";

export const getCurrencies = async (activeCategory) => {
  const result = await fetchCategories();
  return result.data.categories[activeCategory].products[0].prices;
}

const mapStateToProps = (state) => ({
  activeCurrency: state.currencies.activeCurrency,
});


export default connect(mapStateToProps)(getCurrencies);
