import React from "react";
import styles from "./Cart.module.css"
import GroupOrder from "./GroupOrder";
import Title from "./Title"
import CartItem from "./CartItem"
import {AppContext} from "./AppContext"

class Cart extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            // count: 1,
            cart : [],
            total:{
                totalPrice: (this.props.cartList.reduce((prev, curr) => {
                    return prev + curr.prices[this.props.activeCurrency].amount * curr.count
                }, 0)).toFixed(2),
                totalCount: this.props.cartList.reduce((prev, curr) => {
                    return prev + curr.count
                }, 0)
            }
        }
    }

    render() {
        const {cartList, selectedCurrency, activeCurrency } = this.context
        const {count} = this.state

        return (
            <main className={styles.container}>
                <Title className={styles.titleHeader}>Cart</Title>
                {cartList &&
                    cartList.map((orderItem, id) => (
                        <section className={styles.content} key={id}>
                            <CartItem
                                orderItem={orderItem}
                                count={count}
                                id={orderItem.id}
                                // countIncrease={countIncrease}
                                // countDecrease={countDecrease}
                                // activeCurrency={activeCurrency}
                                // onSelectAttribute={this.props.onSelectAttribute}
                                // activeAttribute={this.props.activeAttribute}
                                // activeAttributeInd={this.props.activeAttributeInd}
                            />
                        </section>
                    ))
                }
                <div className={styles.groupOrder}>
                    <GroupOrder
                        cartList={cartList}
                        count={count}
                        totalCount={this.props.totalCount}
                        totalPrice={this.props.totalPrice}
                        total={this.state.total}
                        activeItem={this.props.activeItem}
                        activeCurrency={activeCurrency}
                        selectedCurrency={selectedCurrency}
                        activeAttribute={this.props.activeAttribute}
                        activeAttributeInd={this.props.activeAttributeInd}
                    />
                </div>
            </main>
        )
    }
}

Cart.contextType = AppContext;

export default Cart
