import React from "react";
import styles from "./Category.module.css"
import ProductCard from "../components/ProductCard";

class Category extends React.Component {

    render() {
        // console.log(this.props)
        const {productCards, activeItem, categories, onClick} = this.props

        let cards = this.props.productCards[activeItem].products?.map(product => (
            <ProductCard key={product.id} product={product}/>
        ))

        return (
            <main className={styles.container}>
                <h1
                    className={styles.title}>{this.props.productCards[activeItem].name}</h1>
                <section className={styles.productCards}>
                    <div className={styles.grid}>
                        {cards}
                    </div>
                </section>
            </main>
        )
    }
}

export default Category;

