import React from "react";
import { Route, Routes } from "react-router-dom";
import Category from "./pages/Category";
import Header from "./pages/Header";
import Cart from "./pages/Cart";
import PDP from "./pages/Pdp";

import { AppContext } from "./components/AppContext";
import Modal from "./components/Modal";
import CartOverlay from "./components/CartOverlay";

import { fetchCategories } from "./redux/fetchCategories";
import { connect } from "react-redux";

import { setCurrencies } from "./redux/currencies/slice";
import { setProductCards} from "./redux/categories/slice";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modalShow: false,
      count: 1,
      productCards: [this.renderProductCards],
      activeCategory: 0,
      products: [this.renderProductCards],
      openPopup: false,
      activeCurrency: 0,
      selectedCurrency: "$",
      cartList: this.getCartFromLSn(),
      activeAttributeItem: 0,
      activeAttributeIndex: 0,
      activeAttribute: "",
      total: {
        totalPrice: 0,
        totalCount: 0,
      },
    };
  }

  renderProductCards = async () => {
    try {
      const result = await fetchCategories();
      const productCardsItem = result.data.categories;
      console.log(productCardsItem)
      this.props.setProductCards(productCardsItem)

      this.setState({
        productCards: productCards,
      });
    } catch (err) {
      console.log(err);
    }
  };

  componentDidMount() {
    this.renderProductCards();
    this.getCurrencies()
  }

  // componentDidUpdate(prevProps, prevState) {
  //   if(prevProps.productCards !== this.props.productCards) {
  //     this.renderProductCards();
  //   }
  // }

  getCartFromLSn = () => {
    const data = localStorage.getItem("cart");
    return data ? JSON.parse(data) : [];
  };

  onSelectCategories = (index) => {
    this.setState({
      activeCategory: index,
    });
  };
  //
  getCurrencies = async () => {
    try {
      const result = await fetchCategories();
      const currenciesList =
        result.data.categories[this.state.activeCategory].products[0].prices;
      this.props.setCurrencies(currenciesList);
      console.log(currenciesList)
    } catch (err) {
      console.log(err);
    }
  }

  onSelectCurrencies = (index) => {
    const { currencies } = this.props;
    this.setState({
      activeCurrency: index,
      selectedCurrency: currencies[index].currency.symbol,
      openPopup: false,
    });
  };

  onOpenPopup = () => {
    this.setState({ openPopup: !this.state.openPopup });
  };

  onSelectAttribute = (attribute, el, index) => {
    this.setState({
      activeAttributeIndex: index,
      activeAttribute: attribute,
      activeAttributeItem: attribute.items[index],
    });
  };

  onAddToCart = (product) => {
    const {
      cartList,
      activeAttribute,
      activeAttributeItem,
      activeAttributeIndex,
    } = this.state;
    let newProduct;

    if (!product.isInCart && product.inStock) {
      newProduct = {
        attributes: product.attributes,
        activeAttribute: activeAttribute,
        activeAttributeItem: activeAttributeItem,
        activeAttributeIndex: activeAttributeIndex,
        brand: product.brand,
        prices: product.prices,
        gallery: product.gallery,
        id: product.id,
        inStock: product.inStock,
        name: product.name,
        count: 1,
        isInCart: false,
      };
    }

    cartList.forEach((el) => {
      if (el.id === newProduct.id) {
        newProduct.isInCart = true;
      }
    });

    if (!newProduct.isInCart && newProduct.inStock) {
      newProduct.isInCart = true;
      localStorage.setItem("cart", JSON.stringify([...cartList, newProduct]));
      this.setState({
        cartList: JSON.parse(localStorage.getItem("cart")),
      });
    } else if (newProduct.isInCart) {
      cartList.forEach((el, index, arr) => {
        if (el.activeAttributeItem.id !== newProduct.activeAttributeItem.id) {
          localStorage.setItem(
            "cart",
            JSON.stringify([...cartList, newProduct])
          );
          this.setState({
            cartList: JSON.parse(localStorage.getItem("cart")),
          });
        } else if (
          el.activeAttributeItem.id === newProduct.activeAttributeItem.id
        ) {
          this.setState({
            count: ++newProduct.count,
          });
          newProduct = { ...newProduct, count: ++el.count };
          localStorage.setItem("cart", JSON.stringify(cartList));
          this.setState({
            cartList: JSON.parse(localStorage.getItem("cart")),
          });
          arr.length = index + 1;
        }
      });
    }
  };

  deleteCartItem = (product, id) => {
    const { cartList } = this.state;
    const index = cartList.findIndex(
      (x) =>
        x.id === id &&
        x.activeAttributeItem.id === product.activeAttributeItem.id
    );
    cartList.splice(index, 1);
    localStorage.setItem("cart", JSON.stringify(cartList));
  };

  countIncrease = (product, id) => {
    const { cartList } = this.state;

    if (product.id === id) {
      this.setState({
        count: ++product.count,
      });
    }

    let newProductChanged = { ...product, count: ++this.state.count };

    cartList.forEach((el) => {
      if (el.id === product.id) {
        product = newProductChanged;
      }
    });
    localStorage.setItem("cart", JSON.stringify(cartList));
  };

  countDecrease = (product, id) => {
    const { cartList } = this.state;

    if (product.id === id) {
      this.setState({
        count:
          product.count - 1 > 0
            ? --product.count
            : this.deleteCartItem(product, id),
      });
    }

    let newProductChanged = { ...product, count: --this.state.count };

    cartList.forEach((el) => {
      if (el.id === product.id) {
        product = newProductChanged;
      }
    });
    localStorage.setItem("cart", JSON.stringify(cartList));
  };

  toggleModal = () => {
    this.setState({
      modalShow: !this.state.modalShow,
    });
  };

  onHidePopup = () => {
    document.body.style.overflow = "";
    this.setState({
      modalShow: false,
    });
  };

  render() {

    console.log(this.props.productCards)
    console.log(this.props.currencies)
    console.log(this.renderProductCards)



    const totalPrice = this.state.cartList
      .reduce((prev, curr) => {
        return (
          prev + curr.prices[this.state.activeCurrency].amount * curr.count
        );
      }, 0)
      .toFixed(2);

    const totalCount = this.state.cartList.reduce((prev, curr) => {
      return prev + curr.count;
    }, 0);

    const {
      productCards,
      activeCurrency,
      cartList,
      count,
      currencies,
      activeCategory,
      selectedCurrency,
      total,
      openPopup,
      modalShow,
      activeAttributeItem,
      activeAttribute,
      activeAttributeIndex,
    } = this.state;

    // const{productCards} = this.props

    const {
      deleteCartItem,
      countIncrease,
      countDecrease,
      onHidePopup,
      onSelectAttribute,
      getCartFromLS,
      onSelectCategories,
      onOpenPopup,
      onSelectCurrencies,
      toggleModal,
      onAddToCart,
    } = this;

    return (
      <AppContext.Provider
        value={{
          totalCount,
          modalShow,
          totalPrice,
          productCards,
          activeCurrency,
          currencies,
          openPopup,
          activeAttributeIndex,
          cartList,
          count,
          activeCategory,
          selectedCurrency,
          total,
          activeAttributeItem,
          activeAttribute,
          deleteCartItem,
          countIncrease,
          countDecrease,
          onHidePopup,
          onSelectAttribute,
          getCartFromLS,
          onSelectCategories,
          onOpenPopup,
          onSelectCurrencies,
          toggleModal,
          onAddToCart,
        }}
      >
        <div>
          <Header onOpenPopup={this.onOpenPopup} />
          <Routes>
            <Route path="/" element={<Category />} />
            <Route
              path="/cart"
              element={
                <Cart
                  activeCurrency={this.state.activeCurrency}
                  cartList={this.state.cartList}
                />
              }
            />
            <Route
              path="/:cardId"
              element={<PDP activeCurrency={this.state.activeCurrency} />}
            />
          </Routes>
          <Modal
            className={
              modalShow
                ? (document.body.style.overflow = "hidden")
                : (document.body.style.overflow = "")
            }
            close={toggleModal}
            shown={modalShow}
          >
            <CartOverlay />
          </Modal>
        </div>
      </AppContext.Provider>
    );
  }
}

// export default App;

const mapStateToProps = (state) => ({
  currencies: state.currencies.currencies,
  productCards: state.categories.productCards,
  // activeCurrency: state.currencies.activeCurrency,
  // selectedCurrency: state.currencies.selectedCurrency,
  // openPopup: state.currencies.openPopup,
});

const mapDispatchToProps = { fetchCategories, setCurrencies, setProductCards };

export default connect(mapStateToProps, mapDispatchToProps)(App);
