import React from "react";
import {Link} from "react-router-dom";
import styles from "./CartOverlay.module.css"
import CartOverlayItem from "../CartOverlayItem";
import Title from "../Title"
import Button from "../Button"
import {AppContext} from "../AppContext"

import {connect} from "react-redux";
import { onClosePopup, onOpenPopup } from "../../redux/currencies/slice";
import { setModalShow, toggleModal } from "../../redux/modal/slice";
import {actionsRef} from '../Actions'
import {currenciesRef} from '../Currencies'

class CartOverlay extends React.PureComponent {
    constructor(props) {
        super(props);
        this.modalRef = React.createRef();
    }

    // handleClickOutside = ({ target }) => {
    //     // if (this.modalRef.current && !this.modalRef.current.contains(target)) {
