    //     }
    // };

    handleClickOutside = ({ target }) => {
        console.log(this.props.modalShow)
        if (this.modalRef.current && !this.modalRef.current.contains(target) && (actionsRef.current && !actionsRef.current.contains(target))
          && (currenciesRef.current && !currenciesRef.current.contains(target))
        ) {

            // this.props.setModalShow(false)
            this.props.toggleModal();
            console.log('clickCur')
        }
    };

    // handleClickOutside = ({ target }) => {
    //     // if (this.modalRef.current && !this.modalRef.current.contains(target)) {
    //     if (this.modalRef.current && !this.modalRef.current.contains(target)) {
    //         this.props.setModalShow(false)
    //         this.props.toggleModal();
    //         console.log('click555')
    //     }
    // };

    // handleClickOutside = ({ target }) => {
    //     // if (this.modalRef.current && !this.modalRef.current.contains(target)) {
    //     if (actionsRef.current && !actionsRef.current.contains(target)) {
    //         this.props.setModalShow(false)
    //         console.log('click555')
    //     } else if (this.modalRef.current && !this.modalRef.current.contains(target)) {
    //         this.props.toggleModal();
    //         // this.props.setModalShow(true)
    //         // this.props.onClosePopup(true);
    //         console.log('click333')
    //     }
    // };

    // handleClickOutside = ({ target }) => {
    //
    //
    //     if(this.modalRef.current && !this.modalRef.current.contains(target) && this.props.modalShow) {
    //         console.log(this.props.modalShow)
    //         this.props.toggleModal();
    //         this.props.onClosePopup(true);
    //         console.log('click555')
    //     } else if (this.modalRef.current && !this.modalRef.current.contains(target)) {
    //         this.props.toggleModal();
    //         // this.props.onOpenPopup();
    //         // this.props.onClosePopup(true);
    //         console.log('click333')
    //     }
    // };

    componentDidMount() {
        document.addEventListener("mousedown", this.handleClickOutside);
    }

    componentWillUnmount() {
        document.removeEventListener("mousedown", this.handleClickOutside);
    }


    render() {
        console.log(this.modalRef)
        console.log(actionsRef)
        console.log(currenciesRef)
        const {totalCount, totalPrice, onHidePopup} = this.context
        const { activeCurrency, cartList} = this.props
        const symbol = cartList[0]?.prices[activeCurrency].currency.symbol;
        return(
            <div ref={this.modalRef}>
                <main className={styles.container}>
                    <header className={styles.header}>
                        <Title className={styles.title}>My Bag,</Title>
                        <span className={styles.totalCount}>{totalCount} items</span>
                    </header>
                    <div className={styles.content}>
                        {
                            cartList &&
                            cartList.map((cartItem, id) => (
                                <CartOverlayItem
                                    cartItem={cartItem} key={id} id={id}
                                />
                            ))
                        }
                    </div>
                    <div className={styles.total}>
                        <span className={styles.totalName}>Total</span>
                        <span className={styles.totalPrice}>{symbol}{totalPrice}</span>
                    </div>
                    <div className={styles.groupButton}>
                        <Link to="/cart">
                            <Button className={styles.buttonViewBag} onClick={onHidePopup} >VIEW BAG</Button>
                        </Link>
                        <Button className={styles.buttonCheckOut}>CHECK OUT</Button>
                    </div>
                </main>
            </div>
        )
    }
}

CartOverlay.contextType = AppContext;

// export default CartOverlay

const mapStateToProps = (state) => ({
    activeCurrency: state.currencies.activeCurrency,
    cartList:state.cartList.cartList,
    modalShow: state.modal.modalShow,
});

const mapDispatchToProps = { onOpenPopup, onClosePopup, toggleModal, setModalShow };

export default connect(mapStateToProps, mapDispatchToProps)(CartOverlay);
