import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

import { fetchProductItem } from "../fetchProduct";



export const fetchProduct = createAsyncThunk(
  "pdp/fetchByStatus",
  async () => {
    const { data } = await fetchProductItem();
    return data.product;
  }
);

const attributes = [fetchProduct].attributes;
const prices = [fetchProduct].prices[this.props.activeCurrency].amount;
const symbol = [fetchProduct].prices[this.props.activeCurrency].currency.symbol;

const initialState = {
  activeCurrency: 0,
  selectedCurrency: "$",
  openPopup: false,
  currencies: [],
  status: "loading",
  product: [fetchProduct],
  currentImgId: 0,
  attributes: attributes,
  prices: prices,
  symbol: symbol
};

export const pdpSlice = createSlice({
  name: "pdp",
  initialState,
  reducers: {
    onSelectCurrencies: (state, action) => {
      state.activeCurrency = action.payload;
      state.selectedCurrency = state.currencies[action.payload].currency.symbol;
      state.openPopup = false
    },

    onOpenPopup: (state) => {
      state.openPopup = !state.openPopup;
    },

    onClosePopup: (state, action) => {
      state.openPopup = action.payload;
    },

    setCurrencies: (state, action) => {
      state.currencies = action.payload;
    },

    onChangeImage: (state, action) => {
      state.currentImgId = action.payload;
    },

  },
  extraReducers: (builder) => {
    builder.addCase(fetchProduct.pending, (state) => {
      console.log("Идет отправка")
      state.status = 'loading'

    })
    builder.addCase(fetchProduct.fulfilled, (state, action) => {
      console.log("Получение")
      state.product = action.payload
      state.status = 'success'
    })
    builder.addCase(fetchProduct.rejected, (state) => {
      state.status = 'error'
      state.product = []
    })
  },
});

export const { onChangeImage, setCurrencies,onSelectCurrencies, onOpenPopup, onClosePopup } = pdpSlice.actions;

export default pdpSlice.reducer;

