import React from "react";
import styles from "../pages/Pdp.module.css"
import Title from "../components/Title"
import Button from "../components/Button"
import Image from "../components/Image"
import Attributes from "../components/Attributes";
import {apolloClient} from "../index";
import {gql} from "@apollo/client"
import {useParams} from "react-router-dom";
import {AppContext} from "../components/AppContext"

export function withRouter(Children) {
    return (props) => {
        const match = {params: useParams()};
        return <Children {...props} match={match}/>
    }
}

class PDP extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            product: [this.renderPDP],
            currentImgId: 0,
        }
    }

    renderPDP = async () => {
        try {
            const result = await apolloClient
            .query({
                query: gql`
                    query Product($productId: String!) {
                        product(id: $productId) {
                            id
                            name
                            inStock
                            gallery
                            description
                            category
                            prices {
                                amount
                                currency {
                                    symbol
                                    label
                                }
                            }
                            brand
                            attributes {
                                id
                                name
                                type
                                items {
                                    id
                                    value
                                    displayValue
                                }
                            }
                        }
                    }
                `, variables: {productId: this.props.match.params['cardId'].substring(1)}
            });

            const product = result.data.product;
            const attributes = result.data.product.attributes
            let prices = result.data.product.prices[this.props.activeCurrency].amount
            let symbol = result.data.product.prices[this.props.activeCurrency].currency.symbol

            this.setState({
                product: product,
                attributes: attributes,
                prices: prices,
                symbol: symbol
            });

        } catch (err) {
            console.log(err);
        }
    };

    componentDidMount() {
        this.renderPDP();
        console.log(this.state.product)

        this.setState({
            attributes: this.state.attributes,
            // product:  this.renderPDP(),
        })
        console.log(this.state.attributes)
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevState.attributes !== this.state.attributes &&
        prevState.product !== this.state.product) {
            // console.log(prevState.attributes)
            // console.log(this.state.attributes)
            console.log(this.state.product)
            // console.log(this.state.attributes[0].items[0])
            console.log('changed')
            // this.renderPDP();
          this.setState({
             attributes: this.state.product.attributes,
              product: this.renderPDP(),
          })
            // this.renderPDP()
        }
    }

    onChangeImage = (id) => {
        this.setState({
            currentImgId: id
        })
    }

    // onSelectAttribute = (attribute, id,  el, index) => {
    //     // const {attribute} = this.props
    //
    //     console.log(attribute)
    //     console.log(el)
    //     console.log(attribute.items[index])
    //     console.log(index)
    //
    //     console.log(id)
    //
    //     if (attribute.items[index] === el && attribute.id === id) {
    //         this.setState({
    //             activeAttributeItem: attribute.items[index],
    //             activeAttribute: attribute
    //         })
    //     }
    //
    //
    // }

    render() {
        // localStorage.setItem('cart', JSON.stringify(this.state.cartList) )
        const{onAddToCart, onSelectAttribute} = this.context
        const{product, attributes, prices, symbol, currentImgId}= this.state
        let description = `${product.description}`.replace(/(\<(\/?[^>]+)>)/g, '')

        // console.log(product)
        console.log(attributes)

        return (
            <main className={styles.container}>
                <section className={styles.blockImg}>
                    {product.gallery &&
                        product.gallery.map((img, id) => (
                            <Image
                                onClick={() => this.onChangeImage(id)}
                                key={id}
                                className={styles.blockImgItem}
                                width={79}
                                height={80}
                                src={img}
                            />
                        ))
                    }
                </section>
                <section className={styles.blockGroup}>
                    {product.gallery && (
                        <Image
                            className={styles.groupImg}
                            width={610}
                            height={511}
                            src={product.gallery[currentImgId]}
                            alt={product.name}
                        />)}
                    <div className={styles.groupChoice}>
                        <Title className={styles.titleBrand}>{product.brand}</Title>
                        <Title className={styles.titleName}>{product.name}</Title>
                        <div className={styles.attributes}>
                            <Attributes
                                cartItem={product}
                                activeAttributeItem={this.state.activeAttributeItem}
                                activeAttribute={this.state.activeAttribute}
                                onSelectAttribute={onSelectAttribute}
                                attributes={attributes}
                                attributeName={styles.attributeName}
                                attributeSize={styles.attributeSize}
                                attributeColor={styles.attributeColor}
                            />
                        </div>
                        <div className={styles.groupChoicePrice}>
                            <h5 className={styles.h5}>PRICE:</h5>
                            <div className={styles.price}>
                                {symbol}{prices}
                            </div>
                        </div>
                        <Button className={styles.button}
                                onClick = {() => onAddToCart(product)}
                        >ADD TO CART</Button>
                        <p className={styles.description}>
                            {description}
                        </p>
                    </div>
                </section>
            </main>
        )
    }
}

PDP.contextType = AppContext;

export default withRouter(PDP)
