import React from "react";
import {Route, Routes} from "react-router-dom";
import Category from "./pages/Category"
import Header from "./pages/Header"
import Cart from "./pages/Cart";
import {apolloClient} from "./index";
import {gql} from "@apollo/client";
import PDP from "./pages/Pdp";
import {AppContext} from "./components/AppContext"
import Modal from "./components/Modal"
import CartOverlay from "./components/CartOverlay"

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isInCart: false,
            modalShow: false,
            count: 1,
            productCards: [this.renderCards],
            activeItem: 0,
            products: [this.renderCards],
            openPopup: false,
            activeCurrency: 0,
            selectedCurrency: '$',
            cartList: this.getCartFromLS(),
            activeAttributeItem: 0,
            activeAttributeIndex: 0,
            activeAttribute: '',
            total: {
                totalPrice: 0,
                totalCount: 0
            }
        }
    }

    renderCards = async () => {
        try {
            const result = await apolloClient
            .query({
                query: gql`
                    query Query {
                        categories {
                            name
                            products {
                                id
                                name
                                category
                                prices {
                                    currency {
                                        symbol
                                        label
                                    }
                                    amount
                                }
                                brand
                                attributes {
                                    items {
                                        id
                                        value
                                        displayValue
                                    }
                                    id
                                    name
                                    type
                                }
                                inStock
                                gallery
                                description
                            }
                        }
                    }
                `
            });
            const categories = result.data.categories;
            const currenciesList = result.data.categories[this.state.activeItem].products[0].prices

            this.setState({
                productCards: categories,
                currencies: currenciesList
            });

        } catch (err) {
            console.log(err);
        }
    }

    componentDidMount() {
        this.renderCards();
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevState.count !== this.state.count) {
            // console.log('Ok')
        }
    }

    getCartFromLS = () => {
        const data = localStorage.getItem('cart')
        return data ? JSON.parse(data) : [];
    }

    onSelectCategories = index => {
        this.setState({
            activeItem: index,
        })
    }

    onSelectCurrencies = (index) => {
        const {currencies} = this.state
        this.setState({
            activeCurrency: index,
            selectedCurrency: currencies[index].currency.symbol,
            openPopup: false,
        })
    }

    onOpenPopup = () => {
        this.setState({openPopup: !this.state.openPopup})
    }

    // onSelectAttribute = ( attribute, id, el, index) => {
    onSelectAttribute = (attribute, el, index) => {
        // if(attribute.id === id) {
            this.setState({
                activeAttributeIndex: index,
                activeAttribute: attribute,
                activeAttributeItem: attribute.items[index]
            })
        // }

        // el = {...el,isActive: true}
        // console.log(this.state.cartList)
        // console.log(id)
        // console.log(el)
        // console.log(index)
        // console.log(attribute.items[index])
    }

    onAddToCart = (product) => {
        const {cartList, activeAttribute, activeAttributeItem, activeAttributeIndex} = this.state;
        // console.log(product)
        let newProduct;

        // console.log(activeAttribute)
        // console.log(activeAttributeItem)

        if (product.inStock && !product.isInCart) {
            newProduct = {
                attributes: product.attributes,
                activeAttribute: activeAttribute,
                activeAttributeItem: activeAttributeItem,
                activeAttributeIndex: activeAttributeIndex,
                brand: product.brand,
                prices: product.prices,
                gallery: product.gallery,
                id: product.id,
                inStock: product.inStock,
                name: product.name,
                count: 1,
                isInCart: false
            }

        }
        // console.log(newProduct)

        cartList.forEach((el) => {
            // if (el.id === newProduct.id ) {
            if (el.id === newProduct.id ) {
                newProduct.isInCart = true;
                // this.setState({
                //     count: ++el.count,
                // })
            }
        })
        // console.log(newProduct)
        // console.log(cartList)

        // cartList.forEach((el) => {
        //     console.log( el.activeAttributeItem )
        //     console.log(newProduct.activeAttributeItem)
        //     if (el.isInCart && el.inStock ) {
        //         this.setState({
        //             count: ++this.state.count,
        //         })
        //         let newProductChanged = {...newProduct, count: this.state.count}
        //
        //         cartList.forEach((el) => {
        //             if (el.id === newProduct.id) {
        //                 newProduct = newProductChanged
        //             }
        //         })
        //         localStorage.setItem('cart', JSON.stringify(cartList))
        //     }  else if (!newProduct.isInCart && newProduct.inStock && el.activeAttributeItem !== newProduct.activeAttributeItem) {
        //     newProduct.isInCart = true
        //     localStorage.setItem('cart', JSON.stringify([...cartList, newProduct]))
        //     this.setState({
        //         cartList: JSON.parse(localStorage.getItem('cart')),
        //     })
        // }
        // })

        if (newProduct.isInCart && newProduct.inStock) {
            console.log("in")
            // newProduct.isInCart = true;
            cartList.forEach((el) => {
                if(el.activeAttributeItem.id !== newProduct.activeAttributeItem.id) {
                    console.log("!==")
                    console.log(el.activeAttributeItem)
                    console.log(newProduct.activeAttributeItem)

                    // this.setState({
                    //     count: ++el.count,
                    // })

                    this.setState({
                        cartList: JSON.parse(localStorage.getItem('cart')),
                    })
                    localStorage.setItem('cart', JSON.stringify([...cartList, newProduct]))

                } else if(el.activeAttributeItem.id === newProduct.activeAttributeItem.id) {
                    console.log("===")
                    // this.setState({
                    //     count: ++this.state.count,
                    // })

                    this.setState({
                        count: ++el.count,
                    })
                    // newProduct = {...newProduct, count: this.state.count}
                    // console.log(this.state.count)
                    localStorage.setItem('cart', JSON.stringify([...cartList,{...newProduct, count: this.state.count}]))
                    // this.setState({
                    //     cartList: JSON.parse(localStorage.getItem('cart')),
                    // })
                    // this.setState({
                    //     count: ++this.state.count,
                    // })
                    //
                    // let newProductChanged = {...newProduct, count: this.state.count}
                    //
                    // cartList.forEach((el) => {
                    //     if (el.id === newProduct.id) {
                    //         newProduct = newProductChanged
                    //     }
                    // })

                    // localStorage.setItem('cart', JSON.stringify(cartList))
                }
            })
            // this.setState({
            //     count: ++this.state.count,
            // })
            //
            // let newProductChanged = {...newProduct, count: this.state.count}
            //
            // cartList.forEach((el) => {
            //     if (el.id === newProduct.id) {
            //         newProduct = newProductChanged
            //     }
            // })

            // localStorage.setItem('cart', JSON.stringify(cartList))

        } else if (!newProduct.isInCart && newProduct.inStock) {
            console.log("out")
            newProduct.isInCart = true
            localStorage.setItem('cart', JSON.stringify([...cartList, newProduct]))
            this.setState({
                cartList: JSON.parse(localStorage.getItem('cart')),
            })
        }
    }

    deleteCartItem = (id) => {
        const {cartList} = this.state
        const index = cartList.findIndex(x => x.id === id);
        cartList.splice(index,1)
        localStorage.setItem('cart',JSON.stringify(cartList))
    }

    countIncrease = (product, id) => {
        const {cartList} = this.state

        if (product.id === id) {
            this.setState({
                count: ++product.count,
            })
        }

        let newProductChanged = {...product, count: ++this.state.count}

        cartList.forEach((el) => {
            if (el.id === product.id) {
                product = newProductChanged
            }
        })
        localStorage.setItem('cart', JSON.stringify(cartList))
    }

    countDecrease = (product, id) => {
        const {cartList} = this.state

        if (product.id === id) {
            this.setState({
                count: product.count - 1 > 0 ? --product.count : this.deleteCartItem(id),
            })
        }

        let newProductChanged = {...product, count: --this.state.count}

        cartList.forEach((el) => {
            if (el.id === product.id) {
                product = newProductChanged
            }
        })
        localStorage.setItem('cart', JSON.stringify(cartList))
    }

    toggleModal = () => {
        this.setState({
            modalShow: !this.state.modalShow
        })
    }

    onHidePopup = () => {
        document.body.style.overflow = "";
        this.setState({
            modalShow: false
        })
    }

    render() {
        let totalPrice = (this.state.cartList.reduce((prev, curr) => {
            return prev + curr.prices[this.state.activeCurrency].amount * curr.count
        }, 0)).toFixed(2)

