                            cartList={this.state.cartList}
                        />}
                        />
                        <Route path="/:cardId" element={<PDP
                            activeCurrency={this.state.activeCurrency}
                        />}
                        />
                    </Routes>
                    <Modal
                        className={modalShow ? document.body.style.overflow = "hidden" :
