import React from "react";
import styles from "./Category.module.css";
import ProductCard from "../../components/ProductCard";
import Title from "../../components/Title";
import { AppContext } from "../../components/AppContext";

import { connect } from "react-redux"
import {increment} from '../../redux/currencies/slice'
import {fetchCategories} from "../../redux/currencies/fetchCategories";
import { fetchPizzas } from "../../redux/currencies/asinkAction2";

class Category extends React.PureComponent {
  render() {
    const { productCards, activeCategory } = this.context;

    return (
      <div>
        <div className={`${styles.container} 
                 `}>
          <Title onClick={() => this.props.increment()} className={styles.title}>{productCards[activeCategory].name}</Title>
          <section className={styles.productCards}>
            <div className={styles.grid}>
              {
                productCards[activeCategory].products?.map((product) => (
                  <ProductCard
                    key={product.id}
                    product={product}
                  />
                ))
              }
            </div>
          </section>
          <div className={styles.containerCartOverlay}>
          </div>
        </div>
      </div>
    );
  }
}

Category.contextType = AppContext;

// export default Category;


const mapStateToProps = (state) => ({
  currencies: state.currencies.currencies,
  count: state.currencies.value
});


const mapDispatchToProps = { increment };

export default connect(mapStateToProps, mapDispatchToProps)(Category);
