import React from "react";
import {Route, Routes} from "react-router-dom";
import {CategoryAll, Header} from "./components"
import Cart from "./components/Cart";
import {apolloClient} from "./index";
import {gql} from "@apollo/client";
import categories from "./components/Categories";

// import React from "@types/react";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            productCards: [this.renderCards],
            activeItem: 0,
            prices: this.renderPrices
        }
    }

    renderCards = async () => {
        try {
            const result = await apolloClient
            .query({
                query: gql`
                    #                    query Query {
                    #                        categories {
                    #                            name
                    #                            products {
                    #                                id
                    #                                name
                    #                                inStock
                    #                                gallery
                    #                                description
                    #                                category
                    #                                brand
                    #                            }
                    #                        }
                    #                    }
                    query Query {
                        categories {
                            name
                            products {
                                id
                                name
                                category
                                prices {
                                    currency {
                                        symbol
                                    }
                                    amount
                                }
                                brand
                                attributes {
                                    items {
                                        id
                                        value
                                        displayValue
                                    }
                                    id
                                    name
                                    type
                                }
                                inStock
                                gallery
                                description
                            }
                        }
                    }
                `
            });

            const categories = result.data.categories;

            this.setState({
                productCards: categories

            });

        } catch (err) {
            console.log(err);
        }
        ;
    }

    renderPrices = async () => {
        try {
            const result = await apolloClient
            .query({
                query: gql`
                    query Product($productId: String!) {
                        product(id: $productId) {
                            id
                            name
                            inStock
                            gallery
                            description
                            category
                            attributes {
                                id
                                name
                                type
                                items {
                                    id
                                    value
                                    displayValue
                                }
                            }
                            prices {
                                currency {
                                    symbol
                                    label
                                }
                                amount
                            }
                            brand
                        }
                    }
                `
            });

            const prices = result.data;

            this.setState({
                prices: prices

            });

        } catch (err) {
            console.log(err);
        }
        ;
    }

    componentDidMount() {
        this.renderCards();
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevState.productCards !== this.state.productCards) {
            console.log(this.state.productCards)
            // this.setState({
            //     productCards: this.categories[2]
            // });
            // this.renderCards2()
        }
    }

    clickOnButton = () => {
        alert(123)
    }

    render() {
        console.log(this.state.productCards[0].products)
        console.log(this.state.prices)
        return (
            <div>
                <h2>My first Apollo app 🚀</h2>
                <Header onClick={this.clickOnButton}
                        productCard={this.state.productCards}
                        activeItem={this.state.activeItem}
                />
                {/*<GroupOrder/>*/}
                {/*<Actions outline onClick={clickOnButton}/>*/}
                {/*<Header productCard={props.productCards}/>*/}
                {/*<Categories />*/}
                {/*<CategoryAll />*/}
                {/*<Cart />*/}
                <Routes>
                    <Route path="/" element={<CategoryAll/>}/>
                    <Route path="cart" element={<Cart onClick={this.clickOnButton}
                                                      productCard={this.state.productCards}
                                                      activeItem={this.state.activeItem}
                    />}/>
                </Routes>
            </div>
        );
    }
}

export default App;
