import React from "react";
import {gql} from '@apollo/client';
import styles from "./CategoryAll.module.css"
import ProductCard from "../components/ProductCard";
import {apolloClient} from "./../index"
import Header from "../components/Header";

class CategoryAll extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            productCards: this.renderCards,
        }
    }

    componentDidMount() {
        this.renderCards();

    }


    componentDidUpdate(prevProps, prevState) {
        if (prevState.productCards !== this.state.productCards) {
            console.log('pokemons state has changed.')
        }
    }

    renderCards = async () => {
        try {
            const result = await apolloClient
            .query({
                query: gql`
                    query Query {
                        categories {
                            name
                            products {
                                id
                                name
                                inStock
                                gallery
                                description
                                category
                                brand
                            }
                        }
                    }
                `
            });

            const categories = result.data.categories;


            this.setState({
                productCards: categories
            });

        } catch (err) {
            console.log(err);
        }
        ;
    }

    renderCards2 = async () => {
        try {
            const result = await apolloClient
            .query({
                query: gql`
                    query Query {
                        categories {
                            name
                            products {
                                id
                                name
                                inStock
                                gallery
                                description
                                category
                                brand
                            }
                        }
                    }
                `
            });

            const categories = result.data.categories;


            this.setState({
                productCards: categories[1]
            });

        } catch (err) {
            console.log(err);
        }
        ;
    }

    handleClick = () => {
        this.setState({productCards: this.renderCards2 });
        // console.log(this.categories[2])
        console.log('target')
    }
    press = () => {
        let productCard2 = this.state.productCards[1];
        this.setState({productCards: productCard2});
    }
    // changeState = () => {
    //     this.setState({
    //         productCards: this.renderCards
    //     });
    // }
    // renderHeaders(i) {
    //     return (
    //         <Header
    //             value={this.state.value[i]}
    //             onClick={() => this.handleClick()}
    //         />
    //     );
    // }

    render() {
        console.log(this.state.productCards)

        const {productCards} = this.state.productCards;

        console.log(productCards)
        console.log(productCards)

        const cards = this.state.productCards.products?.map(product => (
            <ProductCard key={product.id} product={product}/>
        ))

        return (
            <main className={styles.container}>
                <Header productCard={this.state.productCards} func={this.press}/>
                <h1 className={styles.title}>{this.state.productCards.name}</h1>
                <section className={styles.productCards}>
                    <div className={styles.grid}>
                        {cards}
                    </div>
                </section>
            </main>
        )
    }
}

export default CategoryAll;

