import React from "react";
import {ReactComponent as HeaderLogo} from '../SVG/headerLogo.svg';
import {ReactComponent as HeaderBasket} from '../SVG/headerBasket.svg';
import {Link} from "react-router-dom";
import styles from "./Header.module.css"
import Categories from "./Categories";
import Actions from "./Actions"
import {AppContext} from "./AppContext"


class Header extends React.Component {
    render() {

        const{onSelectCategories,  openPopup, productCards, activeItem, currencies, onOpenPopup, activeCurrency,  selectedCurrency,
            onSelectCurrencies, toggleModal,  categories, totalCount   } = this.context
        // console.log(this.props.activeItem)
        // console.log(this.props.categories)
        return (
            <div className={styles.container}>
                <Link to="/">
                    <div className={styles.navigation}>
                        <Categories
                            onClick={(index) => {
                                onSelectCategories(index)
                            }}
                            productCards={productCards}
                            activeItem={activeItem}
                            categories={categories}
                        />
                    </div>
                </Link>
                <div className={styles.logo}>
                    <HeaderLogo/>
                </div>

                    <Actions
                             onSelectCurrencies={onSelectCurrencies}
                             onOpenPopup={onOpenPopup}
                             productCards={productCards}
                             activeItem={activeItem}
                             currencies={currencies}
                             totalCount={totalCount}
                             openPopup={openPopup}
                             activeCurrency={activeCurrency}
                             selectedCurrency={selectedCurrency}
                             toggleModal={toggleModal}
                    />
            </div>
        )

    }
}

Header.contextType = AppContext;

export default Header;
