import React from "react";
import styles from './Categories.module.css'

class Categories extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            // activeItem: 0,
            productCard: this.props.productCards
        }
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevState.productCards !== this.state.productCards) {
            this.onSelectCategories()
        }
    }

    onSelectCategories = index => {
        this.setState({
            activeItem: index,
            productCard: this.props.productCards[index]
        })
        console.log(this.props.productCards[index])
    }

    render() {
        console.log(this.props.productCards)
        const {productCards} = this.props
        // console.log(productCards[2])
        console.log(this.props.activeItem)
        // console.log(this.state.productCard)


        return (
            <div>
                <ul className={styles.categories}>
                    {
                        productCards &&
                        productCards.map((category, index) => (
                            // select &&
                            // select.map((category, index) => (
                            <li onClick={() => this.props.onSelectCategories(index)}
                                key={`${category.name}_${index}`}
                                className={`${styles.categoriesItem} 
                                ${this.props.activeItem === index ? styles.active : ''}`}>
                                {category.name}
                            </li>))
                    }
                </ul>
            </div>
        )
    }
}

export default Categories
