import React from "react";
import { Route, Routes } from "react-router-dom";
import Category from "./pages/Category";
import Header from "./pages/Header";
import Cart from "./pages/Cart";
import PDP from "./pages/Pdp";

import { AppContext } from "./components/AppContext";
import Modal from "./components/Modal";
import CartOverlay from "./components/CartOverlay";

import { fetchCategories } from "./redux/fetchCategories";
import { connect } from "react-redux";
import { onOpenPopup, setCurrencies } from "./redux/currencies/slice";
import { setProductCards, fetchProductCards } from "./redux/categories/slice";
import { countPlus, setCartList } from "./redux/cartList/slice";
import { setModalShow, toggleModal } from "./redux/modal/slice";
import {
  setActiveAttribute,
  setActiveAttributeIndex,
  setActiveAttributeItem,
} from "./redux/attributes/slice";
import { getCartFromLS } from "./redux/cartList/getCartFromLs";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      // activeAttributeItem: 0,
      // activeAttributeIndex: 0,
      // activeAttribute: "",
      // total: {
      //   totalPrice: 0,
      //   totalCount: 0,
      // },
    };
  }

  componentDidMount() {
    this.renderProductCards();
    this.getCurrencies();
  }

  renderProductCards = async () => {
    //     const result = await fetchCategories();
    //     const productCards = result.data.categories;
    //     console.log(productCards)
    // this.props.setProductCards(productCards)
    await this.props.fetchProductCards();
    // this.setState({
    //   productCards: productCards,
    // });
    // }
  };

  // getProductCards = () => {
  //   this.renderProductCards()
  // }

  getCurrencies = async () => {
    const { activeCategory, setCurrencies } = this.props;
    const result = await fetchCategories();
    const currenciesList =
      result.data.categories[activeCategory].products[0].prices;
    setCurrencies(currenciesList);
  };

  onSelectAttribute = (attribute, el, index) => {
    const {
      setActiveAttributeItem,
      setActiveAttributeIndex,
      setActiveAttribute,
    } = this.props;

    return (
      setActiveAttributeIndex(index),
      setActiveAttribute(attribute),
      setActiveAttributeItem(attribute.items[index])
    );
  };

  onAddToCart = (product) => {
    const { cartList, setCartList } = this.props;
    const { activeAttribute, activeAttributeItem, activeAttributeIndex } =
      this.state;
    let newProduct;

    if (!product.isInCart && product.inStock) {
      newProduct = {
        attributes: product.attributes,
        activeAttribute: activeAttribute,
        activeAttributeItem: activeAttributeItem,
        activeAttributeIndex: activeAttributeIndex,
        brand: product.brand,
        prices: product.prices,
        gallery: product.gallery,
        id: product.id,
        inStock: product.inStock,
        name: product.name,
        count: 1,
        isInCart: false,
      };
    }

    cartList.forEach((el) => {
      if (el.id === newProduct.id) {
        newProduct.isInCart = true;
      }
    });

    if (!newProduct.isInCart && newProduct.inStock) {
      newProduct.isInCart = true;
      localStorage.setItem("cart", JSON.stringify([...cartList, newProduct]));
      setCartList(JSON.parse(localStorage.getItem("cart")));
      // this.setState({
      //   cartList: JSON.parse(localStorage.getItem("cart")),
      // });
    } else if (newProduct.isInCart) {
      cartList.forEach((el, index, arr) => {
        if (el.activeAttributeItem.id !== newProduct.activeAttributeItem.id) {
          localStorage.setItem(
            "cart",
            JSON.stringify([...cartList, newProduct])
          );
          setCartList(JSON.parse(localStorage.getItem("cart")));
          // this.setState({
          //   cartList: JSON.parse(localStorage.getItem("cart")),
          // });
        } else if (
          el.activeAttributeItem.id === newProduct.activeAttributeItem.id
        ) {
          this.setState({
            count: ++newProduct.count,
          });
          newProduct = { ...newProduct, count: ++el.count };
          localStorage.setItem("cart", JSON.stringify(cartList));
          setCartList(JSON.parse(localStorage.getItem("cart")));
          // this.setState({
          //   cartList: JSON.parse(localStorage.getItem("cart")),
          // });
          arr.length = index + 1;
        }
      });
    }
  };

  deleteCartItem = (product) => {
    const { cartList } = this.props;

    const cartListNew = cartList.filter((obj) => obj !== product);
    console.log(cartListNew);
    console.log("OK");

    // const index = cartList.findIndex(
    //   (x) =>
    //     x.id === id &&
    //     x.activeAttributeItem.id === product.activeAttributeItem.id
    // );
    // console.log(index)

    // cartList.splice(index, 1);
    localStorage.setItem("cart", JSON.stringify(cartListNew));
    setCartList(JSON.parse(localStorage.getItem("cart")));
  };

  countIncrease = (product, id) => {
    const {setCartList} = this.props;
    const cartList = getCartFromLS()

    // if (product.id === id) {
      // countPlus(product.count);

      // this.setState({
      //   count: ++product.count,
      // });
    // }

    let newProductChanged = { ...product, count: product.count + 1};

    cartList.forEach((el, index) => {
      if (el.id === product.id) {
        console.log(cartList[index].count)
        cartList[index] = newProductChanged;
        console.log(cartList[index].count)
      }
    });
    setCartList(cartList);
    localStorage.setItem("cart", JSON.stringify(cartList));
  };

  countDecrease = (product, id) => {
    const { cartList, setCount } = this.props;

    if (product.id === id) {
      setCount(
        product.count - 1 > 0 ? --product.count : this.deleteCartItem(product)
      );

      // this.setState({
      //   count:
      //     product.count - 1 > 0
      //       ? --product.count
      //       : this.deleteCartItem(product),
      // });
    }

    let newProductChanged = { ...product, count: --this.props.count };

    cartList.forEach((el) => {
      if (el.id === product.id) {
        product = newProductChanged;
      }
    });
    localStorage.setItem("cart", JSON.stringify(cartList));
  };

  onHidePopup = () => {
    document.body.style.overflow = "";
    this.props.setModalShow(false);
  };

  render() {
    // console.log(this.props.count)

    const totalPrice = this.props.cartList
      .reduce((prev, curr) => {
        return (
          prev + curr.prices[this.props.activeCurrency].amount * curr.count
        );
      }, 0)
      .toFixed(2);

    const totalCount = this.props.cartList.reduce((prev, curr) => {
      return prev + curr.count;
    }, 0);

    const { toggleModal, modalShow } = this.props;

    const {
      deleteCartItem,
      countIncrease,
      countDecrease,
      onHidePopup,
      onSelectAttribute,
      onAddToCart,
    } = this;

    return (
      <AppContext.Provider
        value={{
          totalCount,
          totalPrice,
          deleteCartItem,
          countIncrease,
          countDecrease,
          onHidePopup,
          onSelectAttribute,
          onAddToCart,
        }}
      >
        <div>
          <Header />
          <Routes>
            <Route path="/" element={<Category />} />
            <Route path="/cart" element={<Cart />} />
            <Route path="/:cardId" element={<PDP />} />
          </Routes>
          <Modal
            className={
              modalShow
                ? (document.body.style.overflow = "hidden")
                : (document.body.style.overflow = "")
            }
            close={toggleModal}
            shown={modalShow}
          >
            <CartOverlay />
          </Modal>
        </div>
      </AppContext.Provider>
    );
  }
}

const mapStateToProps = (state) => ({
  currencies: state.currencies.currencies,
  productCards: state.categories.productCards,
  activeCurrency: state.currencies.activeCurrency,
  cartList: state.cartList.cartList,
  activeCategory: state.categories.activeCategory,
  modalShow: state.modal.modalShow,
  count: state.cartList.count,
  activeAttributeItem: state.attributes.activeAttributeItem,
  activeAttributeIndex: state.attributes.activeAttributeIndex,
  activeAttribute: state.attributes.activeAttribute,
});
const mapDispatchToProps = {
  fetchCategories,
  setCurrencies,
  setProductCards,
  setCartList,
  onOpenPopup,
  fetchProductCards,
  toggleModal,
  setModalShow,
  countPlus,
  setActiveAttributeItem,
  setActiveAttributeIndex,
  setActiveAttribute,
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
