import React from "react";
import {ReactComponent as VectorCurrency} from '../SVG/VectorCurrency.svg';

import styles from "./Actions.module.css"
import {ReactComponent as HeaderBasket} from "../SVG/headerBasket.svg";

class Actions extends React.Component {
    constructor(props) {
        super(props);
        this.state = ({
            // openPopup: false,
            openPopup: true
        })
    }
    render() {
        let{currencies} = this.props
        // console.log(this.props.productCards[this.props.activeItem].products)
        // let prices = this.props.productCards[this.props.activeItem].products[0].prices
        // let products = this.props.productCards[this.props.activeItem].products
        // const currency = prices.map((price, id) => (
        //     <li key={id}>{price.currency.symbol}</li>
        // ))

        return (
            <div className={styles.actions}>
                <ul
                    // onClick={this.props.clickOnButton}
                    //  className={`${styles.actionsItem} ${this.props.outline ? styles.actionsOutline : ''}`}
                >
                    {/*{prices}*/}
                    $
                    <VectorCurrency />
                </ul>
                {
                    this.state.openPopup && (
                        <div>
                            <ul>
                                {
                                //     products[0].map((product, index) =>(
                                //     product.prices.map((price, index) =>(
                                //         <li
                                //             className={styles.actionsItem}
                                //             key={index}>{price.currency.symbol}</li>
                                //     ))
                                // ))

                                    currencies.map((currency, index) =>(
                                     <li
                                         className={styles.actionsItem}
                                         key={index}>{price.currency.symbol}</li>
                                    ))
                                }

                                {/*<li className={styles.actionsItem}>$ USD</li>*/}
                                {/*<li className={styles.actionsItem}>$ EUR</li>*/}
                                {/*<li className={styles.actionsItem}>A$ AUD</li>*/}
                                {/*<li className={styles.actionsItem}>$</li>*/}
                                {/*<li className={styles.actionsItem}>$ RUB</li>*/}

                            </ul>
                        </div>
                    )

                }
                <div>< HeaderBasket/></div>
            </div>
        )
    }
}


export default Actions;
