    render() {

console.log(this.props )
        return(
            <div className={styles.container}>
                {/*<Link to="/">*/}
                    <div className={styles.navigation} >
                        {/*<div className={styles.navigation} onClick={() => this.props.onClick()}>*/}
                        <Categories
