import React from "react";
import styles from "./Attributes.module.css";
import Attribute from "../Attribute";

import {connect} from "react-redux";
import {
  setActiveAttribute,
  setActiveAttributeIndex,
  setActiveAttributeItem, setAttributes
} from "../../redux/attributes/slice";

class Attributes extends React.PureComponent {

  componentDidMount() {
    const {setAttributes,  attributes} = this.props
    setAttributes(attributes)
  }

  render() {
    const {
      attributeName,
      attributeSize,
      attributeColor,
      attributes,
      onSelectAttribute,
      cartItem,
      cartList,
    } = this.props;
// console.log(cartItem)
console.log(cartItem.attributes)
console.log(attributes)

    return (
      <ul className={styles.ul}>
        {cartItem.attributes &&
          cartItem.attributes.map((attribute, id) => (
            <li key={`${id}_${attribute.items}`}>
              <p className={attributeName}>{attribute.name}</p>
              <Attribute
                // attribute={cartItem.activeAttribute}
                cartItem={cartItem}
                onSelectAttribute={onSelectAttribute}
                attribute={attribute}
                attributeColor={attributeColor}
                attributeSize={attributeSize}
              />
            </li>
          ))}
      </ul>
    );
  }
}

const mapStateToProps = (state) => ({
  attributes: state.attributes.attributes,
  cartList: state.cartList.cartList
});

const mapDispatchToProps = {
  setActiveAttribute,
  setActiveAttributeIndex,
  setActiveAttributeItem,
  setAttributes,
};

export default connect(mapStateToProps, mapDispatchToProps )(Attributes);
