    render() {
        const {
            attributeName,
            attributeSize,
            attributeColor,
            attributes,
            onSelectAttribute,
            activeAttributeItem,
            activeAttribute,
            cartItem
        } = this.props

        console.log(cartItem)

        return (
            // <>
                <ul className={styles.ul}>
                    {
                        attributes &&
                        attributes.map((attribute, id) => (
                            <>
                                <li className={attributeName} key={`${id}_${attribute.name}`}  >
                                    {attribute.name}</li>
                                {/*<li key={`${attribute.id}_${id}`}>*/}
                                <li key={attribute.toString()}>
                                    <Attribute
                                        // key={`${attribute.id}_${attribute.type}`}
                                        // key={id.toString()}
                                        cartItem={cartItem}
                                        // id={cartItem.id}
                                        activeAttributeItem={activeAttributeItem}
                                        activeAttribute={activeAttribute}
                                        onSelectAttribute={onSelectAttribute}
                                        attribute={attribute}
                                        attributeColor={attributeColor}
                                        attributeSize={attributeSize}
                                    />
                                </li>
                            </>
                        ))
                    }
                </ul>
            // </>
        )
    }
}

export default Attributes;
