import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

import { fetchProductItem } from "../fetchProduct";

export const fetchPAttributes = createAsyncThunk(
  "pdp/fetchByStatus",
  async (cardId) => {
    const { data } = await fetchProductItem(cardId);
    return data.product.attributes[0].items;
  }
);

const initialState = {
  status: "loading",
  attributes: [fetchPAttributes],
};

export const attributesSlice = createSlice({
  name: "attributes",
  initialState,
  reducers: {
    setSymbol: (state, action) => {
      state.symbol = action.payload;
    },

  },
  extraReducers: (builder) => {
    builder.addCase(fetchPAttributes.pending, (state) => {
      console.log("Идет отправка")
      state.status = 'loading'

    })
    builder.addCase(fetchPAttributes.fulfilled, (state, action) => {
      console.log("Получение")
      state.attributes = action.payload
      state.status = 'success'
    })
    builder.addCase(fetchPAttributes.rejected, (state) => {
      state.status = 'error'
      
    })
  },
});

export const { setSymbol } = attributesSlice.actions;

export default attributesSlice.reducer;
