import React from "react";
import {ReactComponent as VectorCurrency} from '../SVG/VectorCurrency.svg';
import {Link} from "react-router-dom";

import styles from "./Actions.module.css"
import {ReactComponent as HeaderBasket} from "../SVG/headerBasket.svg";

class Actions extends React.Component {
    constructor(props) {
        super(props);
        this.state = ({
            openPopup: false,
            currencyActive: 0
            // openPopup: true
        })
    }

    onSelectCurrencies = index => {
        this.setState({
            currencyActive: index,
        })
    }

    render() {
        let{currencies} = this.props
        console.log(currencies)

        return (
            <div className={styles.actions}>
                <span
                    onClick={() => this.setState({openPopup: !this.state.openPopup})}
                    //  className={`${styles.actionsItem} ${this.props.outline ? styles.actionsOutline : ''}`}
                >
                    $ <VectorCurrency/>
                </span>
                {
                    this.state.openPopup && (
                        <div>
                            <ul>
                                {
                                    currencies?.map((item, index) => (
                                        <li
                                            onClick={this.state.onSelectCurrencies}
                                            className={styles.actionsItem}
                                            key={index}>
                                            {item.currency.symbol}
                                        </li>
                                    ))
                                }
                            </ul>
                        </div>
                    )
                }
                <Link to="/cart">
                    <div>< HeaderBasket/></div>
                </Link>

            </div>
        )
    }
}


export default Actions;
