  render() {
    const { children, shown, close } = this.props;

    return shown ? (
      <div ref={this.modalRef}
        className={styles.modalBackdrop}
        // onClick={() => {
        //   close();
        // }}
        onClick={this.handleClick}
      >
        <div
          className={styles.modalContent}
          onClick={(e) => {
            e.stopPropagation();
          }}
        >
          {children}
        </div>
      </div>
    ) : null;
  }
}

export default Modal;
