import React from "react";
import {gql} from '@apollo/client';
import styles from "./CategoryAll.module.css"
import ProductCard from "../components/ProductCard";
import {apolloClient} from "./../index"
import Header from "../components/Header";

class CategoryAll extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            productCards: [this.renderCards],
        }
    }

    renderCards = async () => {
        try {
            const result = await apolloClient
            .query({
                query: gql`
                    query Query {
                        categories {
                            name
                            products {
                                id
                                name
                                inStock
                                gallery
                                description
                                category
                                brand
                            }
                        }
                    }
                `
            });

            const categories = result.data.categories;


            this.setState({
                productCards: categories
            });

        } catch (err) {
            console.log(err);
        }
        ;
    }

    componentDidMount() {
        this.renderCards();
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevState.productCards !== this.state.productCards) {
            console.log('this state has changed.')
            console.log(this.state.productCards)
            // this.setState({
            //     productCards: this.categories[2]
            // });
            // this.renderCards2()
        }
    }

    renderCategories = (index) => {
        const cart = this.state.productCards[index].products?.map(product => (
            <ProductCard key={product.id} product={product}/>
        ))
        return cart
    }

    render() {

        console.log(this.state.productCards)
        // console.log(productCards[0])
        // console.log(cards1)

        const cards = this.state.productCards[1].products?.map(product => (
            <ProductCard key={product.id} product={product}/>
        ))

        return (
            <main className={styles.container}>
                {/*<Header productCard={this.state.productCards}/>*/}
                <h1 className={styles.title}>{this.state.productCards.name}Name</h1>
                <section className={styles.productCards}>
                    <div className={styles.grid}>
                        {cards}
                        {/*{this.renderCategories}*/}
                    </div>
                </section>
            </main>
        )
    }
}

export default CategoryAll;

