import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  modalShow: false,
};

export const modalSlice = createSlice({
  name: "modal",
  initialState,
  reducers: {
    toggleModal: (state) => {
      state.modalShow = !state.modalShow;
    },
  },
});

export const { toggleModal } = modalSlice.actions;

export default modalSlice.reducer;
