//                     inStock
//                     gallery
//                     description
//                     category
//                     brand
//                 }
//             }
//         }
//     `
// })
// .then((result) => console.log(result))
    }

    render() {
        client
        .query({
            query: gql`
                query Categories {
                    categories {
                        name
                        products {
                            name
                            inStock
                            gallery
                            description
                            category
                            brand
                        }
                    }
                }
            `
        })
        .then((result) => console.log(result.data))
        console.log()
        const { name }  = this.props.category;
        // const { names }  = this.props.categories[0];
        console.log(name)
        console.log(this.props.categories)
        return(
            <main className={styles.container}>
                <h1 className={styles.title}>Category name:{name}</h1>
                <section className={styles.productCards}>
                    {/*<div className={styles.grid}>*/}
                    {/*    // {result?.categories[0].products?.map(product => (*/}
                    {/*    <ProductCard key={product.id} product={product} />*/}
                    {/*))}*/}
                    {/*</div>*/}
                </section>
            </main>
        )
    }
}

export default Category;

