import { createSlice } from "@reduxjs/toolkit";

import {getCartFromLS} from "./getCartFromLs";

const initialState = {
  cartList: getCartFromLS(),
  count: 1,
};

export const cartListSlice = createSlice({
  name: "cartList",
  initialState,
  reducers: {
    setCartList: (state, action) => {
      state.cartList =  action.payload;
    },

    countDecrease: (state, action) => {
      if(action.payload.count - 1 > 0) {
        const newProductChanged = { ...action.payload, count: action.payload.count - 1}
        state.cartList.forEach((el, index) => {
          if (el.id === action.payload.id) {
            state.cartList[index] = newProductChanged;
          }
        });
        setCartList(state.cartList);
        localStorage.setItem("cart", JSON.stringify(state.cartList));
      } else {
        deleteCartItem(action.payload)
      };
    },

    deleteCartItem: (state, action) => {
      const cartListNew = state.cartList.filter((item) => item.id !== action.payload.id);
      setCartList(cartListNew)
      localStorage.setItem("cart", JSON.stringify(cartListNew));
    },
  },
});

export const { setCartList, countDecrease, deleteCartItem } = cartListSlice.actions;

export default cartListSlice.reducer;
