            id
            name
            inStock
            gallery
            description
            category
            attributes {
                id
                name
                type
                items {
                    displayValue
                    value
                    id
                }
            }
            prices {
                currency {
                    symbol
                    label
                }
            }
            brand
        }
        currencies {
            label
            symbol
        }
    }
`;

const PRICES = gql`
    query GetPrices {
        category {
            name
            products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                    id
                    name
                    type
