import React from "react";
import styles from "./GroupOrder.module.css";
import Button from "./Button"

class GroupOrder extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            orderArr: []
        }

    }

    doOrder = (orders) => {
        // newProduct = {...product, count: 1}
       let {orderArr} = this.state
        orderArr = orders.map((order, id) => {
            order = {...order,
                     count: this.props.count
            }
        })
        return orderArr
    }

    render() {
        const {totalCount, totalPrice, selectedCurrency, activeCurrency, cartList} = this.props
        const tax = (totalPrice * 21/100).toFixed(2)
console.log(cartList)
        return (
            <main className={styles.order}>
                <div>
                    <span className={styles.tax}>Tax 21%:</span>
                    <span className={styles.taxValue}>
                        {selectedCurrency}
                        {tax}
                    </span>
                </div>
                <div>
                    <span className={styles.quantity}>Quantity:</span>
                    <span className={styles.quantityValue}>
                        {totalCount}
                    </span>
                </div>
                <div>
                    <span className={styles.total}>Total:</span>
                    <span className={styles.totalValue}>
                        {selectedCurrency}
                        {totalPrice}
                    </span>
                </div>
                {/*<button onClick={this.increaseCount} className={styles.button}>ORDER</button>*/}
                {/*<button className={styles.button}>ORDER</button>*/}
                <Button className={styles.button} onClick={() => console.log(this.state.orderArr)}>ORDER</Button>
            </main>
        )
    }
}

export default GroupOrder;
