import React from "react";
import styles from "./CartItem.module.css"
import Title from "../Title"
import Count from "../Count"
import Attributes from "../Attributes";
import ImageSlider from "../ImageSlider"
import {AppContext} from "../AppContext"

import {connect} from "react-redux";
import { onClosePopup, onOpenPopup } from "../../redux/currencies/slice";
import { fetchProduct, onChangeImage, setPrices, setSymbol } from "../../redux/pdp/slice";

class CartItem extends React.PureComponent {
    render() {
        const{orderItem, activeCurrency, id, prices, symbol, setPrices, setSymbol} = this.props
        const{  countDecrease , countIncrease} = this.context
        // const amount = orderItem.prices[activeCurrency].amount;
        // const symbol = orderItem.prices[activeCurrency].currency.symbol;
        // setPrices(amount);
        // setSymbol(symbol)
        const amount = prices

        return(
            <main className={styles.container}>
                <section>
                    <div>
                        <Title className={styles.titleBrand}>{orderItem.brand}</Title>
                        <Title className={styles.titleName}>{orderItem.name}</Title>
                    </div>
                    <p className={styles.price}>{symbol}{amount}</p>
                    <div className={styles.choiceSize}>
                        <Attributes
                            cartItem={orderItem}
                            attributes={orderItem.attributes}
                            attributeName={styles.attributeName}
                            attributeSize={styles.attributeSize}
                            attributeColor={styles.attributeColor}
                        />
                    </div>
                </section>
                <section className={styles.groupImg}>
                    <div className={styles.count}>
                        <Count
                            classSize={styles.countSize}
                            children={orderItem.count}
                            countIncrease={countIncrease}
                            countDecrease={countDecrease}
                            id={id}
                            orderItem={orderItem}
                        />
                    </div>
                    <ImageSlider
                        slides={orderItem.gallery}
                        className={styles.slider}
                    />
                </section>
            </main>
        )
    }
};

CartItem.contextType = AppContext;

// export default CartItem;

const mapStateToProps = (state) => ({
    activeCurrency: state.currencies.activeCurrency,
    prices: state.pdp.prices,
    symbol: state.pdp.symbol
});

const mapDispatchToProps = { setPrices, setSymbol }

export default connect(mapStateToProps, mapDispatchToProps)(CartItem);
