import React from "react";
import { Routes, Route, Link } from "react-router-dom";
import {Header, CategoryAll, Actions, Categories} from "./components"
import Category from "./components/Category";
import Cart from "./components/Cart";
import {apolloClient} from "./index";
import {gql} from "@apollo/client";
// import React from "@types/react";

class App extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            productCards:  [this.renderCards],
            // activeItem: 0
        }
    }

    renderCards = async () => {
        try {
            const result = await apolloClient
            .query({
                query: gql`
                    query Query {
                        categories {
                            name
                            products {
                                id
                                name
                                inStock
                                gallery
                                description
                                category
                                brand
                            }
                        }
                    }
                `
            });

            const categories = result.data.categories;


            this.setState({
                productCards: categories
            });

        } catch (err) {
            console.log(err);
        }
        ;
    }

    componentDidMount() {
        this.renderCards();
    }

componentDidUpdate(prevProps, prevState) {
    if (prevState.productCards !== this.state.productCards) {
        console.log(this.state.productCards)
        // this.setState({
        //     productCards: this.categories[2]
        // });
        // this.renderCards2()
    }
}

    clickOnButton =() => {
       alert(123)
   }

   render() {
       return (
           <div>
               <h2>My first Apollo app ðŸš€</h2>
               <Header onClick={this.clickOnButton}
                       productCard={this.state.productCards}
                       // activeItem={this.state.activeItem}
                   />
               {/*<Actions outline onClick={clickOnButton}/>*/}
               {/*<Header productCard={props.productCards}/>*/}
               {/*<Categories />*/}
               {/*<CategoryAll />*/}
               {/*/!*<Category />*!/*/}
               {/*<Cart />*/}
               <Routes>
                   <Route path="/" element={<CategoryAll />} />
                   <Route path="cart" element={<Cart onClick={this.clickOnButton}/>} />
               </Routes>
           </div>
       );
   }


}

export default App;
