import React from "react";
import styles from "./Pdp.module.css"
import GroupTitle from "./GroupTitle";
import ChoiceSize from "./ChoiseSize";
import ChoiceColor from "./ChoiceColor";

class PDP extends React.Component {
    render() {
        return (
            <main className={styles.container}>
                <section className={styles.blockImg}>
                    <img className={styles.blockImgItem} src="" alt="img1"/>
                    <img className={styles.blockImgItem} src="" alt="img2"/>
                    <img className={styles.blockImgItem} src="" alt="img3"/>
                </section>
                <section className={styles.blockGroup}>
                    <img className={styles.groupImg} src="" alt="img main"/>
                    <div className={styles.groupChoice}>
                        <div><GroupTitle /></div>
                        {/*<p className={styles.groupChoiceTitle}>Apollo</p>*/}
                        {/*<p className={styles.groupChoiceLabel}>Running Short</p>*/}
                        {/*<div className={styles.groupChoiceSize}>*/}
                        {/*    <h5 className={styles.h5}>size:</h5>*/}
                        {/*    <div className={styles.choiceSize}>*/}
                        {/*        <div className={styles.choiceSizeItem}>XS</div>*/}
                        {/*        <div className={`${styles.choiceSizeItem} ${styles.active}`}>S</div>*/}
                        {/*        <div className={styles.choiceSizeItem}>M</div>*/}
                        {/*        <div className={styles.choiceSizeItem}>L</div>*/}
                        {/*    </div>*/}
                        {/*</div>*/}
                        <div><ChoiceSize /></div>
                        <div><ChoiceColor /></div>

                        {/*<div className={styles.groupChoiceColor}>*/}
                        {/*    <h5 className={styles.h5}>color:</h5>*/}
                        {/*    <div className={styles.choiceColor}>*/}
                        {/*        <div className={`${styles.choiceColorItem} ${styles.colorItem1}`}></div>*/}
                        {/*        <div className={`${styles.choiceColorItem} ${styles.colorItem2}`}></div>*/}
                        {/*        <div className={`${styles.choiceColorItem} ${styles.colorItem3}`}></div>*/}
                        {/*    </div>*/}
                        {/*</div>*/}
                        <div className={styles.groupChoicePrice}>
                            <h5 className={styles.h5}>PRICE:</h5>
                            <div className={styles.price}>$50.00</div>
                        </div>
                        <button className={styles.button}>ADD TO CART</button>
                        <p className={styles.description}>Description</p>
                    </div>
                </section>
            </main>
        )
    }
}

export default PDP
