import React from "react";
import styles from "./ProductCard.module.css"
import {Link} from "react-router-dom";
import Title from "../Title"
import Image from "../Image"
import Icon from "../Icon"
import {ReactComponent as InCart} from "../../assets/SVG/InCart.svg"
import {AppContext} from "../AppContext"

import {connect} from "react-redux";
import { onClosePopup, onOpenPopup } from "../../redux/currencies/slice";
import { fetchProduct, onChangeImage, setPrices, setSymbol } from "../../redux/pdp/slice";
import { setActiveAttribute, setActiveAttributeIndex, setActiveAttributeItem } from "../../redux/attributes/slice";

class ProductCard extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            isHovered: false
        }
    }

    toggleHover = () => {
        this.setState(prevState => ({isHovered: !prevState.isHovered}))
    }

    onAddToCartFromPLP = (product) => {
        const {setActiveAttributeIndex, setActiveAttributeItem} = this.props
        const { onAddToCart} = this.context


        console.log(product.attributes)

       if (product.attributes[0]) {
           console.log("Yes")
           setActiveAttributeIndex(1)
           setActiveAttributeItem(0)
           console.log(this.props.activeAttributeIndex)
           // console.log(product)
           onAddToCart(product)
       } else if(!product.attributes[0]){
           console.log("No")
           onAddToCart(product)
       }


    }

    render() {
        const { onAddToCart} = this.context
        const {product, activeCurrency } = this.props
        const {isHovered} = this.state
        const {name, gallery, prices, inStock} = product;
        const symbol = prices[activeCurrency].currency.symbol
        const amount = prices[activeCurrency].amount;

        // console.log(product)

        return (
            <div className={`${styles.container} ${isHovered ? styles.shadow : ""}`}
                 onMouseEnter={this.toggleHover} onMouseLeave={this.toggleHover} >
                <Link to={`/ ${product.id}`}>
                    <div className={styles.blockImg}>
                        <Image
                            className={styles.img}
                            width={354}
                            height={330}
                            alt={name}
                            src={gallery[0]}
                        />
                        {inStock ? "" : (<Title className={styles.stock}>OUT OF STOCK</Title>)}
                    </div>
                    <div className={styles.content}>
                        <Title className={styles.title}>{name}</Title>
                        <div className={styles.price}>Price: {symbol} {amount}</div>
                    </div>
                </Link>
                {isHovered && inStock ?
                    (<Icon className={styles.hover} onClick={() => this.onAddToCartFromPLP(product)}><InCart/></Icon>) : null}
            </div>
        )
    }
};

ProductCard.contextType = AppContext;

const mapStateToProps = (state) => ({
    activeCurrency: state.currencies.activeCurrency,
    prices: state.pdp.prices,
    symbol: state.pdp.symbol,
    activeAttributeIndex: state.attributes.activeAttributeIndex
});

const mapDispatchToProps = { setPrices, setSymbol,  setActiveAttributeItem,
    setActiveAttributeIndex, setActiveAttribute, };

export default connect(mapStateToProps, mapDispatchToProps)(ProductCard);
