import React from "react";
import { ReactComponent as HeaderLogo } from "../../assets/SVG/headerLogo.svg";
import { Link } from "react-router-dom";
import styles from "./Header.module.css";
import Categories from "../../components/Categories";
import Actions from "../../components/Actions";

import {connect} from "react-redux";
import { AppContext } from "../../components/AppContext";
import { fetchCategories } from "../../redux/fetchCategories";
import { onOpenPopup, setCurrencies } from "../../redux/currencies/slice";
import { onSelectCategories } from "../../redux/categories/slice";
import { getCart } from "../../redux/cartList/slice";

class Header extends React.PureComponent {
  render() {
    const { onSelectCategories} = this.props;

    return (
      <div className={styles.container}>
        <div className={styles.navigation}>
          <Categories
            onClick={(index) => {
              onSelectCategories(index);
            }}
          />
        </div>
        <div className={styles.logo}>
          <Link to="/">
            <HeaderLogo />
          </Link>
        </div>
        <Actions />
      </div>
    );
  }
}

Header.contextType = AppContext;

// export default Header;

const mapDispatchToProps = { onSelectCategories };

export default connect(mapDispatchToProps)(Header);
