import React from "react";
import {gql} from '@apollo/client';
import styles from "./CategoryAll.module.css"
import ProductCard from "../components/ProductCard";
import {apolloClient} from "./../index"
import Header from "../components/Header";

class CategoryAll extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
        }
    }

    componentDidMount() {
        const renderCards = async () => {

            const result = await apolloClient
            .query({
                query: gql`
                    query Query {
                        categories {
                            name
                            products {
                                id
                                name
                                inStock
                                gallery
                                description
                                category
                                brand
                            }
                        }
                    }
                `
            });

            // const categories = result.data.categories;


            this.setState({
                isLoaded: true,
                items: result.data.items
            });


        }

    }

    render() {
        console.log(items)
        const cards = items.products?.map(product => (
            <ProductCard key={product.id} product={product}/>
        ))
        const { error, isLoaded, items } = this.state;
        if (error) {
            return <div>Ошибка: {error.message}</div>;
        } else if (!isLoaded) {
            return <div>Загрузка...</div>;
        } else {
            // return (
            //     <ul>
            //         {items.map(item => (
            //             <li key={item.id}>
            //                 {item.name} {item.price}
            //             </li>
            //         ))}
            //     </ul>
            console.log(items)
            return (
                <main className={styles.container}>
                    <Header productCard={this.state.productCards} func={this.handleClick}/>
                    <h1 className={styles.title}>{this.state.productCards.name}</h1>
                    <section className={styles.productCards}>
                        <div className={styles.grid}>
                            {cards}
                        </div>
                    </section>
                </main>
        )
//             );
        }
    }
}
