import { createSlice } from '@reduxjs/toolkit'
import { AppContext } from "../../components/AppContext";
import Actions from "../../components/Actions";
// const {currencies} = this.context
const initialState = {
  activeCurrency: 0,
  selectedCurrency: "$",
  openPopup: false,
  currencies
}

export const currenciesSlice = createSlice({
  name: 'currencies',
  initialState,
  reducers: {
    addItem: (state, action) => {
      const findItem = state.items.find((obj) => obj.id === action.payload.id)
      if (findItem) {
        findItem.count++
      } else {
        state.items.push({
          ...action.payload,
          count: 1,
        })
      }

      // state.totalPrice = calcTotalPrice(state.items)
    },
    onSelectCurrencies: (state, action) => {
      // const {currencies} = this.context
      state.activeCurrency = action.payload
      state.selectedCurrency = state.currencies[action.payload].currency.symbol
      state.openPopup = false
    }

  },
})

currenciesSlice.contextType = AppContext;

export const { addItem, onSelectCurrencies } = currenciesSlice.actions

export default currenciesSlice.reducer
