import React from "react";
import {gql} from '@apollo/client';
import styles from "./CategoryAll.module.css"
import ProductCard from "../components/ProductCard";
import {apolloClient} from "./../index"
import Header from "../components/Header";

class CategoryAll extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            productCards: this.renderCards,
            value: "ALL",
        }
    }

    componentDidMount() {
        this.renderCards()
    }

    renderCards = async () => {
        try {
            const result = await apolloClient
            .query({
                query: gql`
                    query Query {
                        categories {
                            name
                            products {
                                id
                                name
                                inStock
                                gallery
                                description
                                category
                                brand
                            }
                        }
                    }
                `
            });

            const cards = result.data.categories[0];
            console.log(cards.name)

            this.setState({
                productCards: cards
            });

        } catch (err) {
            console.log(err);
        }
        ;
    }

    handleClick(i) {
        const navs = this.state.value;
        navs[i] = 'X';
        this.setState({value: navs});
    }

    render() {
        const cards = this.state.productCards.products?.map(product => (
            <ProductCard key={product.id} product={product}/>
        ))

        return (
            <main className={styles.container}>
                <Header value={this.props.value}/>
                <h1 className={styles.title}>{this.state.productCards.name}</h1>
                <section className={styles.productCards}>
                    <div className={styles.grid}>
                        {cards}
                    </div>
                </section>
            </main>
        )
    }
}

export default CategoryAll;

