import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

import { fetchCategories } from "../fetchCategories";

export const fetchProductCards = createAsyncThunk(
  "currencies/fetchByStatus",
  async () => {
    const { data } = await fetchCategories();
    return data.categories;
  }
);

const initialState = {
  // activeCurrency: 0,
  // selectedCurrency: "$",
  // openPopup: false,
  // activeCategory: 0,
  // productCards: [renderProductCards()],
  status: "loading",
  productCards: [fetchProductCards],
};

export const categoriesSlice = createSlice({
  name: "categories",
  initialState,
  reducers: {
    // setProductCards: (state, action) => {
    //   state.productCards = action.payload;
    // },
  },
  extraReducers: (builder) => {
    builder.addCase(fetchProductCards.pending, (state) => {
      console.log("Идет отправка")
      state.status = 'loading'
      // state.productCards = []
    })
    builder.addCase(fetchProductCards.fulfilled, (state, action) => {
      console.log("Получение")
      state.productCards = action.payload
      state.status = 'success'
    })
    builder.addCase(fetchProductCards.rejected, (state) => {
      state.status = 'error'
      state.productCards = []
    })
  },
});

export const { setProductCards } = categoriesSlice.actions;

export default categoriesSlice.reducer;
