import React from "react";
import { ReactComponent as HeaderLogo } from '../SVG/headerLogo.svg';
import { ReactComponent as HeaderBasket } from '../SVG/headerBasket.svg';
import {Link} from "react-router-dom";
import styles from "./Header.module.css"

class Header extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: "ALL",
        };
    }

    addActiveClass(e){
        console.log('target')
        // return e.target.value
        this.setState((e)=> {
           value: e.target.value
        })
        console.log(this.value)
    }

    render() {
        return(
            <div className={styles.container}>
                {/*<div className={styles.navigation} onClick={() => console.log('click')}>*/}
                <div className={styles.navigation} onClick={(e) => this.addActiveClass(e)}>

                    <div className={`${styles.navigationItem} ${styles.active}`}>
                       ALL:{this.value}
                    </div>
                    <div className={styles.navigationItem}>
                        Clothes:{this.value}
                    </div>
                    <div className={styles.navigationItem}>
                        Tech:{this.value}
                    </div>

                    {/*<div className={`${styles.navigationItem} ${styles.active}`}>*/}
                    {/*    <Link to="/">ALL{this.state.value}</Link>*/}
                    {/*</div>*/}
                    {/*<div className={styles.navigationItem}>*/}
                    {/*    <Link to="/clothes">CLOTHES</Link>*/}
                    {/*</div>*/}
                    {/*<div className={styles.navigationItem}>*/}
                    {/*    <Link to="/tech">TECH</Link>*/}
                    {/*</div>*/}
                </div>
                <div className={styles.logo}>
                    <HeaderLogo/>
                </div>
                <div className={styles.actions}>
                    <div className={styles.actionsItem}>$</div>
                    <div>< HeaderBasket/></div>
                </div>
            </div>
        )
    }
}

export default Header;
