import React from "react";
import { useQuery, gql } from '@apollo/client';
import styles from "./ProductCard.module.css"

const CARDS = gql`
    query GetCards($productId: String!) {
        categories {
            name
            products {
                id
                name
                inStock
                gallery
                description
                category
                brand
            }
        }
        category {
            name
        }
        product(id: $productId) {
            id
            name
            inStock
            gallery
            description
            category
            attributes {
                id
                name
                type
                items {
                    displayValue
                    value
                    id
                }
            }
            prices {
                currency {
                    label
                    symbol
                }
                amount
            }
            brand
        }
        currencies {
            label
            symbol
        }
    }
`;


class ProductCard extends React.Component {
    constructor(props) {
        super(props);
        this.state= {
            product: props.product,

        }
    }

    render() {
        const { loading, error, data } = useQuery(CARDS, {variables: {productId}});
        if (loading) return 'Loading...';
        if (error) return `Error! ${error.message}`;
        console.log(data)

        const cards =() => {};

        const { name, category, gallery, prices} = this.props.product;
        console.log(this.name)
        return(
            <div className={styles.container}>
                <div className={styles.blockImg}>
                    <img className={styles.img} src={gallery[0]} alt="Photo product"/>
                </div>
                <div className={styles.content}>
                    <div className={styles.title}>Title:{name}</div>
                    <div className={styles.price}>Price: {category}</div>
                </div>
            </div>
        )
    }
};

