    render() {
   const someRes = client
        .query({
            query: gql`
                query Query {
                    categories {
                        name
                        products {
                            id
                            name
                            inStock
                            gallery
                            description
                            category
                            brand
                        }
                    }
                }
            `
        })
        // .then((result) => console.log(result.data.categories[0].products))
        .then((result => {
                    result.data = result,
                <main className={styles.container}>
                    <h1 className={styles.title}>Category name:{result.categories[0].name}</h1>
                    <section className={styles.productCards}>
                        <div className={styles.grid}>
                            // {result?.data.categories[0].products?.map(product => (
                            <ProductCard key={product.id} product={result.data.categories.products} />
                        ))}
                        </div>
                    </section>
                </main>

        }));

        const { name }  = this.props.category;
        // const { names }  = this.props.categories[0];
        console.log(name)
        console.log(this.props.categories)
        return(
            <div>
                {someRes}
            </div>


            // <main className={styles.container}>
            //     <h1 className={styles.title}>Category name:{name}</h1>
            //     {/*<section className={styles.productCards}>*/}
            //     {/*    <div className={styles.grid}>*/}
            //     {/*        // {data?.categories[0].products?.map(product => (*/}
            //     {/*        <ProductCard key={product.id} product={product} />*/}
            //     {/*    ))}*/}
            //     {/*    </div>*/}
            //     {/*</section>*/}
            // </main>
        )
    }
}

export default Category;

