import React from "react";
import styles from "./Count.module.css"

class Count extends React.Component {


    countIncrease = (product, id) => {
       
        if(product.id === id) {
            this.setState({
                count: ++product.count
            })
        }
    }

    countDecrease = (product, id) => {
        const {deleteCartItem} = this.props;

        if (product.id === id) {
            this.setState({
                count: product.count - 1 > 0 ? --product.count : deleteCartItem(id),
            })
        }
    }
    render() {
        // console.log(this.props)
        const{children, classSize, countIncrease, countDecrease,orderItem, id } = this.props
        // const {name, brand} = this.props
        // console.log(cartItem.name)

        return (
            <>
                {/*<p className={className}>{children}</p>*/}
                <button className={`${classSize} ${styles.countItem}`} onClick={() => this.countIncrease(orderItem, id)}>+</button>
                <div className={`${classSize} ${styles.borderNone}`}>{orderItem.count}</div>
                {/*<div className={`${classSize} ${styles.borderNone}`}>{children}</div>*/}
                <button className={`${classSize} ${styles.countItem}`} onClick={() => this.countDecrease(orderItem, id)}>-</button>
            </>
        )
    }
};

export default Count;
