import React from "react";
import { gql } from '@apollo/client';
import styles from "./Category.module.css"
import ProductCard from "./ProductCard";
import {apolloClient} from "./../index"

class Category extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            productCards: [],
        }
    }

    componentDidMount() {
        this.renderCards()

    }

    renderCards = async () => {
        try {
            const res =  await apolloClient
            .query({
                query: gql`
                    query Query {
                        categories {
                            name
                            products {
                                id
                                name
                                inStock
                                gallery
                                description
                                category
                                brand
                            }
                        }
                    }
                `
            });

            const cards = res.data.categories[0];

            this.setState({
                productCards: cards
            });

        } catch (err) {
            console.log(err);
        };
    }

    render() {
        console.log(this.state.productCards)


const cards = this.state.productCards.products?.map(product => (
    <ProductCard key={product.id} product={product} />
))

        return (
            <main className={styles.container}>
                <h1 className={styles.title}>{this.state.productCards.name}</h1>
                <section className={styles.productCards}>
                    <div className={styles.grid}>
                        {cards}
                    </div>
                </section>
            </main>
        )
    }
}

export default Category;
