import React from "react";
import { gql } from '@apollo/client';
import styles from "./Category.module.css"
import ProductCard from "./ProductCard";
import Cards from "../pages/Cards";
import {client} from "./../index"

class Category extends React.Component {
    constructor(props) {
        super(props);
        this.state= {
            name:  this.props.category,
            categories: this.props.categories
        }
    }

    componentDidMount() {
//         client
// .query({
//     query: gql`
//         query Categories {
//             categories {
//                 name
//                 products {
//                     name
//                     inStock
//                     gallery
//                     description
//                     category
//                     brand
//                 }
//             }
//         }
//     `
// })
// .then((result) => console.log(result))
    }

    render() {
   const someRes = client
        .query({
            query: gql`
                query Query {
                    categories {
                        name
                        products {
                            id
                            name
                            inStock
                            gallery
                            description
                            category
                            brand
                        }
                    }
                }
            `
        })
        // .then((result) => console.log(result.data.categories[0].products))
        .then(function (result) {
       return result.data()
   }).then(function (data) {
       data.categories = data,
           console.log(data),
            <main className={styles.container}>
                <h1 className={styles.title}>Category name:{data[0].name}</h1>
                <section className={styles.productCards}>
                    <div className={styles.grid}>
                         {data[0]?.products?.map(product => (
                        <ProductCard key={product.id} product={data.products} />
                    ))}
                    </div>
                </section>
            </main>
        } )



        //
        // fetch(url).then(function(response) {
        //     return response.text()
        // }).then(function(text) {
        //     poemDisplay.textContent = text;
        // });

        const { name }  = this.props.category;
        // const { names }  = this.props.categories[0];
        console.log(name)
        console.log(this.props.categories)
        return(
            <div>
                {someRes}
            </div>


            // <main className={styles.container}>
            //     <h1 className={styles.title}>Category name:{name}</h1>
            //     {/*<section className={styles.productCards}>*/}
            //     {/*    <div className={styles.grid}>*/}
            //     {/*        // {data?.categories[0].products?.map(product => (*/}
            //     {/*        <ProductCard key={product.id} product={product} />*/}
            //     {/*    ))}*/}
            //     {/*    </div>*/}
            //     {/*</section>*/}
            // </main>
        )
    }
}

export default Category;

