import React from "react";
import {ReactComponent as VectorCurrency} from '../SVG/VectorCurrency.svg';
import {Link} from "react-router-dom";

import styles from "./Actions.module.css"
import {ReactComponent as HeaderBasket} from "../SVG/headerBasket.svg";

class Actions extends React.Component {
    constructor(props) {
        super(props);
        this.state = ({
            openPopup: false,
            activeCurrency: 0,
            someCurrent: '$'
        })
    }

    // componentDidMount() {
    //     this.state=({
    //         someCurrent: this.props.currencies[this.state.activeCurrency].currency.symbol
    //     })
    // }

    // componentDidUpdate(prevProps, prevState) {
    //     if (prevState.someCurrent !== this.state.someCurrent) {
    //         console.log('change')
    //         // this.onSelectCurrencies()
    //         // this.setState({
    //         //     someCurrent: this.props.currencies[this.state.activeCurrency].currency.symbol
    //         // });
    //     }
    // }

    onSelectCurrencies = index => {
        this.setState({
            activeCurrency: index,
            // activeCurrency: index ? styles.active : '',
            someCurrent: this.props.currencies[this.state.activeCurrency].currency.symbol,
            openPopup: false,
        })
        // console.log(this.props.currencies[this.state.activeCurrency].amount)
        // console.log(this.state.someCurrent)
    }

    render() {
        let {currencies} = this.props
        let {activeCurrency, openPopup, someCurrent} = this.state
        console.log(currencies)
        console.log(someCurrent)

        return (
            <div className={styles.actions}>
                <span className={styles.popupClosed}
                      onClick={() => this.setState({openPopup: !openPopup})}>
                    {this.state.someCurrent}
                    {/*{this.props.currencies[this.state.activeCurrency].currency.symbol}*/}
                    <VectorCurrency/>
                </span>
                {
                    openPopup && (
                        <div>
                            <ul className={styles.blockCurrencies}>
                                {/*<li className={styles.actionsItem}>{currencies[activeCurrency].currency.symbol}^</li>*/}

                                {
                                    currencies?.map((item, index) => (
                                        <label  key={index}>
                                            <li  key={index}
                                                // onClick={() => this.onSelectCurrencies(index)}
                                                className={`${styles.actionsItem}  
                                            ${activeCurrency === index ? styles.active : ''}`}
                                                key={index}>
                                                {item.currency.symbol}
                                            </li>
                                        </label>
                                    ))
                                }
                            </ul>
                        </div>
                    )
                }
                <Link to="/cart">
                    <div>< HeaderBasket/></div>
                </Link>

            </div>
        )
    }
}


export default Actions;
