                {/*                    ))*/}
                {/*                }*/}
                {/*            </li>*/}
                {/*        </ul>*/}
                {/*    ))*/}
                {/*}*/}



                <ul className={styles.ul}>
                    {
                        this.props.attributes &&
                        this.props.attributes.map((attribute, index) => (
                            <>
                                <li className={styles.attributeName}  key={`${attribute.id}_${attribute.name}`}>
                                    {attribute.name}</li>
                                <li  key={attribute.name}  onClick={() => this.onSelectAttribute(attribute, index)}>
                                    <ul className={styles.sizes}
                                    >
                                        {
                                            attribute.items &&
                                            attribute.items.map((el, index) => (
                                                <li key={el.id}
                                                    // onClick={() => this.onSelectAttribute(attribute, index)}
                                                    className={`${styles.sizeItem}
                                         ${activeAttribute === attribute.id && activeAttributeInd === index ? styles.activeAttribute : ''}`}
                                                    style={{backgroundColor: el.value}}
                                                >{attribute.name === 'Color' ? '': el.value} {index} {el.id}
                                                </li>
                                            ))
                                        }
                                    </ul>
                                </li>
                            </>



                        ))
                    }
                </ul>


            </main>
        )
    }
}

export default Attributes;
